import style from './Card.module.css'
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { addFav, removeFav } from '../../redux/actions';
import React, { useEffect, useState } from "react";


function Card(props) {
console.log(props);

   const [isFav, setIsFav] = useState(false);

   useEffect(()=>{
      props.myFavorites.forEach((fav) => {
         if (fav.id === props.id) {
            setIsFav(true);
         }
      });
   },[props.myFavorites])

   function handleFavorite () {
      if(isFav){
         setIsFav(false);
         props.removeFav(props.id)
      } 
      else if (!isFav){
         setIsFav(true);
         props.removeFav(props)
      }
   }

   return (
      <div className={style.contenedor}>
         {
            isFav ? (
               <button className={style.favorite} onClick={handleFavorite}>‚ù§Ô∏è</button>
            ) : (
               <button className={style.favorite} onClick={handleFavorite}>ü§ç</button>
            )
         }
         <button className={style.close} onClick={()=>props.onClose(props.id)}>x</button>
         <Link to={`/detail/${props.id}`}>
            <h2 className={style.nombre}>{props.name}</h2>
         </Link>
         <img src={props.image} alt='' />
         <div >
            <div className={style.contenedorInfo}>
            <h2 className={style.title}>Status: </h2><h2 className={style.info}>{props.status}</h2>
            </div>
            <div className={style.contenedorInfo}>
            <h2 className={style.title}>Species: </h2><h2 className={style.info}>{props.species}</h2>
            </div>
            <div className={style.contenedorInfo}>
            <h2 className={style.title}>Gender: </h2><h2 className={style.info}>{props.gender}</h2>
            </div>
            <div className={style.contenedorInfo}>
            <h2 className={style.title}>Origin: </h2><h2 className={style.info}>{props.origin}</h2>
            </div>
         </div>
      </div>
   );
}

function mapStateToProps(state) {
   return{
      myfavorites: state.myfavorites,
   }
}

const mapDispatchToProps = (dispatch) => {

   return{
      addFav: (character)=>{dispatch(addFav(character))},
      removeFav: (id)=>{dispatch(removeFav(id))}
   }
   
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
