export default function validation (userData, errors, setErrors) {
    const regexEmail = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/;
    const regexPassword = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9]).{8,}$/;

    if(!regexEmail.test(userData.email)) setErrors({...errors, email:'Email invalido'})
    if(!userData.email) setErrors({...errors, email:'Email Vacio'})
    if(userData.email.lenght >35) setErrors({...errors, email:'Excede el tamano'})

    if(!regexPassword.test(userData.password)) setErrors({...errors, password:'Debe contener min 8 caracteres, un numero y una mayuscula'})
    if(!userData.password) setErrors({...errors, password:'Ingresar password'})

}
