import './App.css';
import Cards from './components/Cards/Cards';
import NavBar from './components/Nav/NavBar';
import About from './components/About/About';
import Detail from './components/Detail/Detail';
import Favorites from './components/Favorites/Favorites';
import Form from './components/Form/Form';
import { useEffect, useState } from 'react';
import axios from 'axios';
import { Routes, Route } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


function App() {
   const URL_BASE = 'http://localhost:3001/rickandmorty/character/';
   const {pathname} = useLocation();
   const navigate = useNavigate();

   const [characters, setCharacters] = useState([]);
   const [access, setAccess] = useState(false);

   //TODO ESTO AHORA ME MANEJA DESDE EL SERVIDOR
   //const email='s@gmail.com';
   //const password='Sasha123'
   // function login (userData) {
   //    if (userData.email === email && userData.password === password){
   //       setAccess(true);
   //       navigate('/home');
   //    }  
   // }

   //LOGIN CON PROMESAS
   // function login(userData) {
   //    const { email, password } = userData;
   //    const URL = 'http://localhost:3001/rickandmorty/login/';
   //    axios(URL + `?email=${email}&password=${password}`).then(({ data }) => {
   //       const { access } = data;
   //       setAccess(data);
   //       access && navigate('/home');
   //    });
   // }

   //LOGIN CON ASYNC/AWAIT
   async function login(userData){
      const { email, password } = userData;
      const URL = 'http://localhost:3001/rickandmorty/login/';
      try {
         const {data} = await axios(URL + `?email=${email}&password=${password}`);
         const { access } = data;
         setAccess(data);
         access && navigate('/home');
      } catch (error) {
         throw new Error(error);
      }

     }

   useEffect(() => {
      !access && navigate('/');
   }, [access]);

   function onClose(id){
      setCharacters(characters.filter(character =>
         character.id !== Number(id)
         ))
   }

   function onSearch(id) {
      if ((characters.find(character => character.id === Number(id)))) {
         return window.alert('¡Personaje repetido!');
         
      }
      axios(`${URL_BASE}${id}`).then(({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } 
         else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
      //lo mismo que arriba pero con fetch
      // fetch(`${URL_BASE}${id}`)
      // .then((response)=>response.json())
      // .then(({data})=>{
      //    if(data.name){
      //       setCharacters((oldChars)=>[...oldChars,data])
      //    }
      // }).catch((err)=>{
      //    console.log(err);
      //    console.log('hubo un error');
      // })
   }

   return (
      <div className='App'>
         {pathname !== '/' && <NavBar onSearch={onSearch}/>
         }
         <Routes>
            <Route path='/' element={<Form login={login}/>}/>
            <Route path='/home' element={<Cards characters={characters} onClose={onClose} />} />
            <Route path='/about' element={<About />} />
            <Route path='/detail/:id' element={<Detail/>}/>
            <Route path='/favorites' element={<Favorites/>}/>
         </Routes>
         
      </div>
   );
}

export default App;
