import { ADD_FAV, REMOVE_FAV } from "./types";
import axios from "axios";

// const addFav = (character) => {
//     return{
//         type: ADD_FAV,
//         payload: character,
//     }
// }

const addFav = (character) => {
   const endpoint = 'http://localhost:3001/rickandmorty/fav';
   return (dispatch) => {
      axios.post(endpoint, character).then(({ data }) => {
         return dispatch({
            type: ADD_FAV,
            payload: data,
         });
      });
   };
};

const removeFav = (id) => {
    return {
        type: REMOVE_FAV,
        payload: id,
    }
}

export {addFav, removeFav}
