export default function validation (userData) {
    const regexEmail = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/;
    const regexPassword = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9]).{8,}$/;
    const errors = {}

    
    if(!regexEmail.test(userData.email)) errors.email = 'Email invalido'
    if(!userData.email) errors.email = 'Email Vacio'
    if(userData.email.lenght >35) errors.email = 'Excede el tamano'

    if(!regexPassword.test(userData.password)) errors.password = 'Debe contener min 8 caracteres, un numero y una mayuscula'
    if(!userData.password) errors.password = 'Ingresar password'


    return errors;
}
