import React, { useState } from "react";
import "./Form.molude.css";


export default function Form () {
    const [useData, setUserData] = useState({
        email: '',
        password: '',
    })

    const [errors, setErros] = useState({
        email: '',
        password: '',
    })

    function validation (useData, key) {
        const regexEmail = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/;
        const regexPassword = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9])(?=.*?[#?!@$%^&*-]).{8,}$/;
        const errors = {}

        if (key === 'email') {
            if(!regexEmail.test(useData.email)) errors.email = 'Email invalido'
            if(!useData.email) errors.email = 'Email Vacio'
            if(useData.email.lenght >35) errors.email = 'Excede el tamano'
        }

        if (key === password) {
            if(!useData.password) errors.password = 'Ingresar password'
            if(useData.lenght >0 || useData.lenght <8) errors.password = 'ingresar min 8 caracteres'
        }

    }

    function handleChange (event) {
        const key = event.target.name;
        const value = event.target.value;
        setUserData({
            ...useData,
            [key]:value,
        })
    }

    return <form className='contenedorForm '  action="">
        <div className="contenedorInputs" >
            <label htmlFor="email">EMAIL</label>
            <input 
            type="text" 
            name="email"
            value={useData.email}
            onChange={handleChange}
            />
        </div>
        <div className="contenedorInputs" >
            <label htmlFor="password">PASSWORD</label>
            <input 
            type="text" 
            name="password" 
            value={useData.password}
            onChange={handleChange}
            />
        </div>
        <button className="btn" type="submit">SUBMIT</button>
    </form>
}
