export default function validation (useData, key) {
    const regexEmail = /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?$/;
    const regexPassword = /^(?=.*?[A-Z])(?=.*?[a-z])(?=.*?[0-9]).{8,}$/;
    const errors = {}

    if (key === 'email') {
        if(!regexEmail.test(useData.email)) errors.email = 'Email invalido'
        if(!useData.email) errors.email = 'Email Vacio'
        if(useData.email.lenght >35) errors.email = 'Excede el tamano'
    }

    if (key === 'password') {
        if(!regexPassword.test(useData.password)) errors.password = 'Debe contener min 8 caracteres, un numero y una mayuscula'
        if(!useData.password) errors.password = 'Ingresar password'
    }

    console.log(errors);

    return errors;
}
